import uuid from 'uuid';
import {GET_TASKS, ADD_TASK, DELETE_TASK} from '../actions/types';


const initialState = {
  tasks: [
    { id: uuid(), name: 'Logo'},
    { id: uuid(), name: 'Ads'},
    { id: uuid(), name: 'Webiste'},
    { id: uuid(), name: 'Cards'},
    { id: uuid(), name: 'Cow'},
    { id: uuid(), name: 'Moon'}
  ]
}

export default function(state = initialState, action) {
  switch(action) {
    case GET_TASKS:
      return {
        ...state
      };
    case DELETE_TASK:
      return {
        ...state,
        tasks: state.task.filter(task => task.id !== action.payload)
      };
    default:
      return state;
  }
}
