import {GET_TASKS, ADD_TASK, DELETE_TASK, TASKS_LOADING} from './types';

export const getTasks = () => {
  return {
    type: GET_TASKS
  };
};

export const deleteTask = (id) => ({
  type: DELETE_TASK,
  payload: id
});

export const addTask = (task) => ({
  type: ADD_TASK,
  payload: task
});

export const setTasksLoading = () => ({
  type: TASKS_LOADING
});
