import uuid from 'uuid';
import {GET_TASKS, ADD_TASK, DELETE_TASK} from '../actions/types';


const initialState = {
  tasks: []
}

const isSameById = payload => ({id}) => payload !== id;

export default function(state = initialState, action) {
  switch(action.type) {
    case GET_TASKS:
      return {
        ...state
      };
    case DELETE_TASK:
      return {
        ...state,
        tasks: state.tasks.filter(isSameById(action.payload))
      };
    case ADD_TASK:
      return {
        ...state,
        tasks: [action.payload, ...state.tasks]
      };
    default:
      return state;
  }
}
